create view post_view as select `post`.`post_id` AS `id`,`post`.`content` AS `content`,`post`.`added` AS `added`,`post`.`approved` AS `approved`,`user`.`user_id` AS `user_id`,`user`.`username` AS `username` from ((`post` join `user_to_post` on((`post`.`post_id` = `user_to_post`.`post_id`))) join `user` on((`user_to_post`.`user_id` = `user`.`user_id`))) union select `post`.`post_id` AS `id`,`post`.`content` AS `content`,`post`.`added` AS `added`,`post`.`approved` AS `approved`,NULL AS `user_id`,`anonymous`.`username` AS `username` from ((`post` join `anonymous_to_post` on((`post`.`post_id` = `anonymous_to_post`.`post_id`))) join `anonymous` on((`anonymous_to_post`.`anonymous_id` = `anonymous`.`anonymous_id`))) order by `added` desc;

